import org.marker.ext.ModuleActivatorimport org.marker.ext.ModuleUtilsimport org.marker.ext.module.ModuleContextimport org.marker.mushroom.beans.Menuimport org.marker.mushroom.ext.menu.MenuUtilsimport org.marker.mushroom.ext.model.ContentModelContextimport org.marker.mushroom.ext.model.impl.GuestBookModelImplimport org.marker.mushroom.ext.plugin.PluginContextimport org.marker.mushroom.ext.plugin.Pluginlet/** * 模块 *  * @author marker *  */public class ModuleActivatorImpl extends ModuleUtils implements ModuleActivator {			private int id;		public void start(ModuleContext context) throws Exception {		ContentModelContext.getInstance().put(new GuestBookModelImpl());		Class<?> clzz = util.loadGroovy("GuestBookPluginlet");		if(null != clzz){			PluginContext pluginContext = PluginContext.getInstance();						pluginContext.put((Pluginlet)clzz.newInstance());						Menu menu = new Menu();			menu.setName("留言管理");			menu.setUrl("/plugin/guestbook/list");			//			MenuUtils menuUtils =MenuUtils.getInstance();//			//			menuUtils.builder(MenuType.L1_CONTENT, menu );//			//			this.id = menu.getId();		}else{					}		// 安装分发器										// 安装特定标签					} 	public void stop(ModuleContext context) throws Exception { 		MenuUtils menuUtils =MenuUtils.getInstance();				menuUtils.remove(this.id);		 	}}