import java.lang.Classimport groovy.lang.GroovyObject;import org.marker.ext.ModuleUtilsimport org.marker.ext.ModuleActivator;import org.marker.ext.module.ModuleContext;import org.marker.mushroom.ext.model.ContentModel;import org.marker.mushroom.ext.model.ContentModelContext;/** * 栏目模块  *  * @author marker */public class ModuleActivatorImpl extends ModuleUtils implements ModuleActivator {		 	/**	 * 启用 	 */	public void start(ModuleContext context) throws Exception { 		GroovyObject cm = util.loadModelObject("model");		ContentModelContext.getInstance().put(cm);	} 			/**	 * 停用	 */	public void stop(ModuleContext context) throws Exception {		ContentModelContext.getInstance().remove("channel"); 	}	}