import org.marker.mushroom.beans.Menu import org.marker.mushroom.ext.menu.MenuUtilsimport org.marker.mushroom.ext.model.impl.ArticleModelImplimport java.lang.Classimport groovy.lang.GroovyObject;import org.marker.ext.ModuleUtilsimport org.marker.ext.ModuleActivator;import org.marker.ext.module.ModuleContext;import org.marker.mushroom.ext.model.ContentModel;import org.marker.mushroom.ext.model.ContentModelContext;/** * 文章模块 *  * @author marker */public class ModuleActivatorImpl extends ModuleUtils implements ModuleActivator {	// 存放菜单ID	private int id;	 	/**	 * 启用 	 */	public void start(ModuleContext context) throws Exception { 		// GroovyObject cm = util.loadModelObject("model");				ContentModelContext.getInstance().put(new ArticleModelImpl());						MenuUtils menuUtils = MenuUtils.getInstance();				// 文章菜单		def menu = [			name: "文章管理",			url : "article/list.do",			desc: "系统基础内容管理",			icon: "fa-th",			type: "6cd7004ae4bc4aaca1435708cb7dd079" // 唯一编码		];		this.id = menuUtils.build(MenuUtils.L1_CONTENT, menu);			} 	/**	 * 停用 	 */	public void stop(ModuleContext context) throws Exception {				ContentModelContext.getInstance().remove("article");						MenuUtils menuUtils = MenuUtils.getInstance();		menuUtils.remove(this.id);			}}